import axios from '@/services/base-api.js';

let basePath = '/products';

async function getAllProducts() {

    try {

        let res = await axios.get(basePath);
        
        return res.data;
    }
    catch (err) {
        console.error(err);
    }
}

async function getProductById(id) {
    
    try { 
        let url = `${basePath}/${id}`;

        let res = await axios.get(url);

        return res.data;
    }
    catch (err) {
        //log everything except user not found
        if(err.response.status != 404) {
            console.error(err);
        }
    }
}

async function addProduct(product) {

    try {
        let url = `${basePath}/add`;
                
        let res = await axios.post(url, product);

        return res.data;
    }
    catch (err) {
        console.error(err);      
    }
}

async function updateProduct(product) {
    try {
        let url = `${basePath}/update`;
                
        let res = await axios.put(url, product);

        return res.data;
    }
    catch (err) {
        console.error(err);      
    }
}

async function getProductCategories() {
    try {
        let url = `${basePath}/categories`;
        
        let res = await axios.get(url);

        return res.data;
    }
    catch (err) {
        console.error(err);      
    }
}

async function getProductsByCategory(category) {
    try {
        let url = `${basePath}/categories/${category}`;        

        let res = await axios.get(url);   

        return res.data;
    }
    catch(err) {
        console.error(err);
    }
}

async function getProductsByName(name) {
    try {   
        let url = `${basePath}/name/${name}`;

        let res = await axios.get(url);

        return res.data;
    }
    catch(err) {
        console.error(err);
    }
}

async function removeBasketProductsFromStore(products) {
    try {   

        let url = `${basePath}/removeBasketItems`;

        let res = await axios.post(url, {
            products: products
        });

        return res.data;
    }
    catch(err) {
        console.error(err);
    }
}

async function getTopRatedProducts() {
    try {   
        let url = `${basePath}/topRated`;

        let res = await axios.get(url);

        return res.data;
    }
    catch(err) {
        console.error(err);
    }
}

export default {
    getAllProducts,
    getProductById,
    addProduct,
    updateProduct,
    getProductCategories,
    getProductsByCategory,
    getProductsByName,
    removeBasketProductsFromStore,
    getTopRatedProducts,
}

import axios from 'axios';
import Host from '@/models/Host';

let host = new Host();

let headers = {
  'cache-control': 'no-cache'
};

let accessToken = localStorage.getItem('jwt-token');
if (accessToken && accessToken !== '') {
    headers.Authorization = accessToken;
};

let baseUrl = `https://${host.ip}:${host.port}`;

const service = axios.create({
  baseURL: baseUrl,
  headers: headers
});

export default service;

export default class Host {

    constructor() {
        // ip:'145.93.140.178',
        this.ip = '192.168.178.213';
        // ip: 'localhost',
        this.port = 8090;
    }
}

import ProductDao from '@/data/productdao.js';

async function getByCategory(category) {

    let products = [];
    if(category == 'All') {
        await ProductDao.getAllProducts()
          .then((response) => {
               products = response;
          });
    }   
    else {
        await ProductDao.getProductsByCategory(category)
          .then((response) => {  
             products = response;
        });
    }
    return products;
}

function toReadableCategories(categories) {

    let formattedCategories = [];
    categories.forEach(category => {
        // replace _ with JS global regex to 'space' and set all chars to lowercase
        let formattedCategory = category.replace(/_/g,' ')
            .toLowerCase();
        
        // first char to uppercase
        formattedCategory = formattedCategory
            .charAt(0).toUpperCase()
            + formattedCategory.slice(1);

        formattedCategories.push(formattedCategory);
    });
    return formattedCategories;
}

 //replace all +'s with spaces ([] eg [+] for calculating symbols)
// = string.replace(/[+]/g, " ");

export default {
    getByCategory,
    toReadableCategories,
}

import ShoppingCartItem from'@/models/ShoppingCartitem.js';

function addToShoppingCart(newProduct, cartItems) {
    
    cartItems = checkExistingBasket(cartItems);

    cartItems = existsInBasket(newProduct, cartItems);

    return cartItems;
} 

function removeFromShoppingCart(productToBeRemoved, cartItems) {
    
    cartItems = checkExistingBasket(cartItems);
    //remove product from shopping cart
    for(let i = 0; i < cartItems.length; i++) {
        let cartItem = cartItems[i];
        let product = cartItem.product;

        if(product.id == productToBeRemoved.id) {
            if(cartItem.quantity > 0) {
                //lower quantity for chosen product
                cartItem.quantity--;
            }
            if(cartItem.quantity == 0) {
                //remove cartItem completely
                let index = cartItems.indexOf(cartItem);
                cartItems.splice(index, 1);
            }
        }  
    
    }
    return cartItems;
}

function checkExistingBasket(products) {
    //create new basket if user doesn't have one  
    return products == null ? [] : products;
}

function calculateBasketCosts(cartItems) {
    let totalCosts = 0;
    cartItems.forEach(cartItem => {
        for(let i = 0; i < cartItem.quantity; i++) {

            let product = cartItem.product;
            totalCosts += product.price;
        }
    });
    return totalCosts.toFixed(2);
}

function getProductIds(cartItems) {

    let productIds = [];

    if(cartItems.length) {
        cartItems.forEach(cartItem => {
            
            let product = cartItem.product;
            for(let i = 0; i < cartItem.quantity; i++) {
                //add each product individually
                productIds.push(product.id);
            }
        });
        return productIds;
    }
    else return [];
}

function getTotalQuantity(cartItems) {
    let totalQuantity = 0;
    cartItems.forEach(cartItem => {
        totalQuantity += cartItem.quantity;
    });
    return totalQuantity;
}

function existsInBasket(p, cartItems) {
    let exists = false;
    cartItems.forEach(item => {
        let product = item.product;
        if(p.id == product.id) {
            item.quantity++;
            exists = true;
            return cartItems;
        }
    }); 
    //still gets triggered otherwise somehow
    if(!exists) {
        cartItems.push(new ShoppingCartItem(p, 1));
        return cartItems;
    }
    else return cartItems;
}

export default {
    addToShoppingCart,
    removeFromShoppingCart,
    checkExistingBasket,
    calculateBasketCosts,
    getProductIds,
    getTotalQuantity,
    existsInBasket,
}

export default class ShoppingCartItem {

    constructor(product, quantity) {
        this.product = product;
        this.quantity = quantity;
    }
}

export default class Product {

    constructor(id, name, imageUrl, description, price, category, stockCount, reviewCount) {
        this.id = id;
        this.name = name;
        this.imageUrl = imageUrl;
        this.description = description;
        this.price = price;
        this.category = category;
        this.stockCount = stockCount;
        this.reviewCount = reviewCount;
    }
}

function removeItem(item, array) {

    var index = array.indexOf(item);

    //remove first occurance
    return array.splice(array.splice(index, 1));
}

function validateProConInput(string, proconCount) {
    //input is not blank or whitespace
    if(string.trim()) {
        if(proconCount >= 4) {
            return false;
        }
        else return true;
    }
    else return false;
}

function validateReviewSubmit(refs) {

    //validate no empty fields 
    let message; 
    refs.forEach(ref => {
        if(ref.name === 'Star Rating') {

            if(ref.value < 1 || ref.value > 5) {
                message ='Please insert a star rating';
            }
        }
        else if(!ref.value || !ref.value.trim()) {
            message = 'Please fill in the ' + ref.name + ' field';
        }
    });
    return message;
}

export default {
    removeItem,
    validateProConInput,
    validateReviewSubmit,
}

import ReviewSummaryItem from '@/models/ReviewSummaryItem.js';

function getAvgRating(reviews) {

    let totalRating = 0;
    reviews.forEach(review => {
        totalRating += review.starRating;
    });
    return (totalRating / reviews.length).toFixed(1);
}

function calculatePercentages(maxRating, reviews, reviewCount) {
    let reviewSummary = [];
    for(let i = maxRating; i > 0; i--) {

        let count = reviews.filter(x => x.starRating == i).length;
        reviewSummary.push(new ReviewSummaryItem(i, count));
    }
    reviewSummary = calculateRatingPercentages(reviewSummary, reviewCount);
    return reviewSummary;
}

//private
function calculateRatingPercentages(reviewSummary, reviewCount) {

    reviewSummary.forEach(ReviewSummaryItem => {
        let percentage = (ReviewSummaryItem.quantity / reviewCount) * 100;
        ReviewSummaryItem.percentage = percentage;
    });
    return reviewSummary;
}

export default {
    getAvgRating,
    calculatePercentages,
}

export default class ReviewSummaryItem {

    constructor(starRating, quantity) {
        this.starRating = starRating;
        this.quantity = quantity;
    }
}

export default class Review {

    constructor(author, title, content, starRating) {

        this.author = author;
                    //send timeStamp
        this.date = Date.now();

        this.title = title;
        this.content = content;

        this.starRating = starRating;

        this.pros = [];
        this.cons = [];
    }

    setProductId(productId) {
        this.productId = productId;
    }

    addPros(...newPros) {
        this.pros.push.apply(this.pros, newPros);
    }

    addCons(...newCons) {
        this.cons.push.apply(this.cons, newCons);
    }


}
